const secsHand = document.querySelector('.second');
const minsHand = document.querySelector('.min');
const hoursHand = document.querySelector('.hour');

function setDate() {
   const date = new Date();


   const mins = date.getMinutes();
   const minsDegrees = ((mins / 60) * 360) + 90;
   minsHand.style.transform = `rotate(${minsDegrees}deg)`;
   
   const secs = date.getSeconds();
   const secsDegrees = ((secs / 60) * 360) + 90;
   secsHand.style.transform = `rotate(${secsDegrees}deg)`;

   const hours = date.getHours();
   const hoursDegrees = ((hours / 12) * 360) + 90;
   hoursHand.style.transform = `rotate(${hoursDegrees}deg)`;

   const electClock  = document.querySelector('.elect-clock');
   electClock.textContent  = date.toLocaleTimeString();

   const days =["Воскресенье", "Понедельник", "Вторник", "Среда", "Четверг", "Пятница", "Суббота"];
   const electDate  = document.querySelector('.elect-days');   
   electDate.textContent  = days[date.getDay()]; 

   const electDay  = document.querySelector('.elect-day');   
   electDay.textContent  = date.getDate(); 
   
    const months = ["января", "февраля", "марта", "мая", "июня", "июля", "августа","сентября", "октября", "ноября", "декабря"];
    const electMonth  = document.querySelector('.elect-month');   
    electMonth.textContent  = months[date.getMonth()+1];    
    

   const electYear  = document.querySelector('.elect-year');
   electYear.textContent  = date.getFullYear();
}
setInterval(setDate, 1000);

const btn = document.querySelector('.mode');
const wrapper = document.querySelector('.wrapper');
btn.addEventListener('click', function(e){
    if(btn.classList.contains('dark')){
        btn.classList.remove('dark');
        wrapper.classList.remove('dark');
        e.target.textContent = 'Темный';
    }else{
        btn.classList.add('dark');
        wrapper.classList.add('dark');
        e.target.textContent = 'Светлый';
    }

});